import { ADD_CUSTOMER } from "../actions/customers";
import { RECEIVE_DATA, CLEAR_DATA } from "../actions/shared"

export default function customers (state = [], action) {
    switch(action.type) {
        case ADD_CUSTOMER :
            state[action.customer.id] = action.customer;
            return state;
        case RECEIVE_DATA :
            let customers = {};
            action.customers.forEach((customer) => (
                customers[customer.id] = customer
            ));
            return customers;
        case CLEAR_DATA:
            return [];
        default:
            return state;
    }
}
