import { SET_ITEMS, ADD_ITEM } from "../actions/items";
import { RECEIVE_DATA, CLEAR_DATA } from "../actions/shared.js"

export default function items (state = {}, action) {
    switch(action.type) {
        case ADD_ITEM :
            state[action.customer.id] = action.customer;
            return state;
        case RECEIVE_DATA :
            let customers = {};
            action.customers.forEach((customer) => (
                customers[customer.id] = customer
            ));
            return customers;
        case CLEAR_DATA:
            return {};
        default:
            return state;
    }
}
