import React, { Component } from 'react';
import {connect} from "react-redux";
import { Card, Form, Button, Table, ButtonGroup, Row } from "react-bootstrap";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faMinus, faPlus } from '@fortawesome/free-solid-svg-icons'


class PurchasePage extends Component {
    constructor(props){
        super(props);

        this.state = {
            newCustomer: '',
            existingCustomer: '',
            selectedItems: {}
        }

        this.handleSelect = this.handleSelect.bind(this);
        this.handleChangeNewCustomer = this.handleChangeNewCustomer.bind(this);
        this.handleChangeQuantity = this.handleChangeQuantity.bind(this);
        this.handlePurchase = this.handlePurchase.bind(this);
    }

    componentDidMount() {
        let selectedItems = {};
        this.props.items.map((item) => {
            selectedItems[item.ItemId] = 0
        });

        this.setState({selectedItems: selectedItems});
    }

    handleChangeNewCustomer(event){
        this.setState({newCustomer: event.target.value});
    }

    handleSelect(event) {
        this.setState({existingCustomer: event.target.value})
    }

    handleChangeQuantity(event){
        let str = event.target.value;
        let ItemId = event.target.id;
        let selectedItems = this.state.selectedItems;

        if(/^(0|[1-9]\d*)$/.test(str)){
            selectedItems[ItemId] = Number(str);
            this.setState({selectedItems: selectedItems});
        }
    }

    handlePurchase(){

    }


    render() {
        const { customers, items } = this.props;

        const plusIcon = <FontAwesomeIcon icon={faPlus} size="xs" color="black"/>
        const minusIcon = <FontAwesomeIcon icon={faMinus} size="xs" color="black"/>

        return(
            <div style={{marginTop: 100}}>
                <Card bg='light' border='info' style={{width: 700, margin: '0 auto', marginTop: 75}}>
                    <Card.Header style={{textAlign: 'center', fontSize: 30}}>Submit New Purchase</Card.Header>
                    <Card.Body>
                        <Form>
                            <Form.Group>
                                <Form.Label>New Customer</Form.Label>
                                <Form.Control style={{width: 250}} placeholder="Customer Name" value={this.state.newCustomer} onChange={this.handleChangeNewCustomer} disabled={this.state.existingCustomer}/>
                                <br/>
                                <Form.Label>Existing Customer</Form.Label>
                                <Form.Control style={{width: 250}} as='select' value={this.state.existingCustomer} onChange={this.handleSelect}>
                                    <option value=''>Select...</option>
                                    {customers.map((customer) => {
                                        return <option key={customer.CustomerId} value={customer.CustomerId}>{customer.CustomerName} - {customer.CustomerId}</option>
                                    })}
                                </Form.Control>
                            </Form.Group>
                            <br/>
                            <Form.Label>Items Purchased</Form.Label>
                            <Table striped bordered hover>
                                <thead>
                                    <tr>
                                        <th></th>
                                        <th>Item Name</th>
                                        <th>Price</th>
                                        <th>Quantity</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {items.map((item) => {
                                        return <tr>
                                            <th><Form.Check/></th>
                                            <th>{item.ItemName}</th>
                                            <th>${item.Price.toFixed(2)}</th>
                                            <th><Form.Control id={item.ItemId} value={this.state.selectedItems[item.ItemId]} onChange={this.handleChangeQuantity} style={{width:50, marginLeft: 20, marginRight: 30}}/></th>
                                        </tr>
                                    })}
                                </tbody>
                            </Table>
                        </Form>
                    </Card.Body>

                    <Card.Footer style={{textAlign: 'center'}}>
                        <Button >Submit</Button>
                    </Card.Footer>
                </Card>
            </div>
        );
    }
}

function mapStateToProps({ customers, items, orders }, props){
    return {
        customers,
        items,
        ...props
    }
}

export default connect(mapStateToProps)(PurchasePage);
