import {createOrder} from "../utils/api";


export const ADD_ORDER = 'ADD_ORDER';


function addOrder(order){
    return {
        type: ADD_ORDER,
        order
    }
}

export function handleNewOrder(orderDetails){
    return (dispatch) => {
        dispatch(createOrder());
    }
}
