import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { BrowserRouter as Router, Route, Redirect, Switch } from "react-router-dom";

import LoadingBar from "react-redux-loading";

import { handleLogin } from "../actions/authedUser";
import { handleInitialData } from "../actions/shared";
import HeaderNavbar from './HeaderNavbar.js';
import LoginModal from './LoginModal.js';



class App extends Component {

    constructor(props){
        super(props);

        this.state = {
            showLogin: null
        }

        this.handleLogin = this.handleLogin.bind(this);
        this.handleLogout = this.handleLogin.bind(this);
        this.openLoginModal = this.openLoginModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
    }

    handleLogin(){

    }

    openLoginModal(){
        this.setState({showLogin: true});
    }

    closeModal(){
        this.setState({showLogin: false});
    }


    render() {
        let modal = <LoginModal showModal={this.state.showLogin} closeModal={this.closeModal}/>


        return (
            <Router>
                <Fragment>
                    <LoadingBar/>
                    <HeaderNavbar openLoginModal={this.openLoginModal}/>
                    <Switch>
                        <Route path='/' exact render={() => (
                            <Redirect to='/home'/>
                        )}/>
                        <Route path='/home'/>
                        <Route path='/purchase'/>
                        <Route path='/receipts'/>
                    </Switch>
                    {modal}
                </Fragment>
            </Router>
        );
    }





}

function mapStateToProps({ customers, items, orders }){
    return {
        customers,
        items,
        orders
    }
}

export default connect(mapStateToProps)(App);
