import { SET_ITEMS, ADD_ITEM, RECEIVE_DATA } from "../actions/items";


export default function items (state = [], action) {
    switch(action.type) {
        case ADD_ITEM :
            return state.concat([action.customer]);
        case RECEIVE_DATA :
            return action.customers;
        default:
            return state;
    }
}
