import { ADD_ORDER, RECEIVE_DATA } from "../actions/orders"


export default function orders (state = {}, action){
    switch(action.type){
        case ADD_ORDER:
            return state.concat([action.order]);
        case RECEIVE_DATA:
            return action.orders;

        default:
            return state

    }

}
