import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { BrowserRouter as Router, Route, Redirect, Switch } from "react-router-dom";

import LoadingBar from "react-redux-loading";

import { handleLogin } from "../actions/authedUser";
import { handleInitialData } from "../actions/shared";
import HeaderNavbar from './HeaderNavbar.js';
import LoginModal from './LoginModal.js';



class App extends Component {

    constructor(props){
        super(props);

        this.state = {
            modal: null
        }
    }


    componentDidMount() {
        const { dispatch, authedUser } = this.props;

        //handleLogin("admin", "password")

        dispatch(handleInitialData("eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpYXQiOjE2MTM5NTQyMTB9._oi-chUsFlD5JBknqZh8WpiMKraciSIbvnxCDShMtps"));
    }

    openLoginModal(){
        this.setState({modal: "login"});
    }

    closeModal(){
        this.setState({modal: null});
    }

    loginUser(username, password){
        handleLogin(username, password);
    }

    render() {

        let modal = null;
        if(this.state.modal==="login"){
            modal = <LoginModal closeModal={this.closeModal()}/>
        }

        return (
            <Router>
                <Fragment>
                    <LoadingBar/>
                    <HeaderNavbar/>
                    <Switch>
                        <Route path='/' exact render={() => (
                            <Redirect to='/home'/>
                        )}/>
                        <Route path='/home'/>
                        <Route path='/purchase'/>
                        <Route path='/receipts'/>
                    </Switch>
                    {modal}
                </Fragment>
            </Router>
        );
    }





}

function mapStateToProps({ customers, items, orders }){
    return {
        customers,
        items,
        orders
    }
}

export default connect(mapStateToProps)(App);
