import React, { Component, Fragment } from 'react';
import { connect } from 'react-redux'
import { BrowserRouter as Router, Route, Redirect, Switch } from "react-router-dom";
import LoadingBar from "react-redux-loading";
import { handleLogin, handleLogout } from "../actions/authedUser";
import HeaderNavbar from './HeaderNavbar.js';
import LoginModal from './LoginModal.js';
import Homepage from "./Homepage";
import PurchasePage from "./PurchasePage";
import ReceiptPage from "./ReceiptPage";



class App extends Component {

    constructor(props){
        super(props);

        this.state = {
            showLogin: null
        }

        this.loginUser = this.loginUser.bind(this);
        this.logoutUser = this.logoutUser.bind(this);
        this.openLoginModal = this.openLoginModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
    }

    loginUser(username, password){
        this.props.dispatch(handleLogin(username, password));
        this.closeModal();
    }

    logoutUser(){
        this.props.dispatch(handleLogout());
    }

    openLoginModal(){
        this.setState({showLogin: true});
    }

    closeModal(){
        this.setState({showLogin: false});
    }


    render() {
        let modal = <LoginModal showModal={this.state.showLogin} closeModal={this.closeModal}/>


        return (
            <Router>
                <Fragment>
                    <LoadingBar/>
                    <HeaderNavbar openLoginModal={this.openLoginModal} logoutUser={this.logoutUser}/>
                    <Switch>
                        <Route path='/' exact render={() => (
                            <Redirect to='/home'/>
                        )}/>
                        <Route path='/home' exact component={Homepage}/>
                        <Route path='/receipts' exact component={ReceiptPage}/>
                        <Route path='/purchase' exact component={PurchasePage}/>
                    </Switch>
                    {modal}
                </Fragment>
            </Router>
        );
    }
}

function mapStateToProps({ customers, items, orders }){
    return {
        customers,
        items,
        orders
    }
}

export default connect(mapStateToProps)(App);
