export const SET_ORDERS = 'SET_ORDERS';
export const NEW_ORDER = 'NEW_ORDER';

function setOrders(orders){
    return {
        type: SET_ORDERS,
        orders
    }
}

function newOrder(order){
    return {
        type: NEW_ORDER,
        order
    }
}
