import React, { Component } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { connect } from 'react-redux'
import { handleLogin } from "../actions/authedUser";


class LoginModal extends Component {

    constructor(props){
        super(props);

        this.state = {
            username: 'admin',
            password: 'password'
        }
    }

    render() {
        const { closeModal, dispatch } = this.props;

        return (
            <Modal>
                <Modal.Header closeButton></Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group>
                            <Form.Label>Username</Form.Label>
                            <Form.Control value={this.state.username} readOnly/>
                        </Form.Group>
                        <Form.Group>
                            <Form.Label>Password</Form.Label>
                            <Form.Control value={this.state.password} readOnly/>
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={closeModal}>Close</Button>
                    <Button variant="primary" onClick={() => dispatch(handleLogin(this.state.username, this.state.password)).then(closeModal())}>Login</Button>
                </Modal.Footer>
            </Modal>
        )
    }
}

function mapStateToProps({ dispatch }){
    return {
        dispatch
    }
}

export default connect(mapStateToProps)(LoginModal);
