export const SET_ORDERS = 'SET_ORDERS';
export const ADD_ORDER = 'ADD_ORDER';

function setOrders (orders) {
    return {
        type: SET_ORDERS,
        orders
    }
}

function addItem (order) {
    return {
        type: ADD_ORDER,
        order
    }
}
