import { ADD_CUSTOMER, SET_CUSTOMERS } from "../actions/customers";


export default function customers (state = [], action) {
    switch(action.type) {
        case ADD_CUSTOMER :
            return state.concat([action.customer]);
        case SET_CUSTOMERS :
            return action.customers;
        default:
            return state;
    }
}
