import { SET_ITEMS, ADD_ITEM } from "../actions/items";
import { RECEIVE_DATA, CLEAR_DATA } from "../actions/shared.js"

export default function items (state = {}, action) {
    switch(action.type) {
        case ADD_ITEM :
            state[action.item.id] = action.item;
            return state;
        case RECEIVE_DATA :
            let items = {};
            action.item.forEach((item) => (
                items[items.id] = item
            ));
            return items;
        case CLEAR_DATA:
            return {};
        default:
            return state;
    }
}
