        }

    }


    render() {

        return(
            <div>
                <Card bg='secondary' border='info' text='light' style={{width: 550, margin: '0 auto', marginTop: 75}}>
                    <Card.Title style={{fontSize: 50, textAlign: 'center'}}>Welcome to the Strider General Store Management App</Card.Title>
                    <Card.Footer style={{textAlign: 'center'}}>
                        {authedUser == null ? (
                            <div>To continue please login <Button style={{marginLeft: "15%"}} variant="outline-info" onClick={openLoginModal}>Login</Button></div>
                        ):(<div/>)}
                    </Card.Footer>
                </Card>
            </div>
        );
    }
}

function mapStateToProps({ customers, items, orders }, props){
    return {
        customers,
        items,
        orders,
        ...props
    }
}

export default connect(mapStateToProps)(PurchasePage);
