import React, { Component } from 'react';
import {connect} from "react-redux";
import { Card, Button } from 'react-bootstrap';


class Homepage extends Component {
    constructor(props){
        super(props);

        this.state = {

        }

    }


    render() {
        const { openLoginModal, authedUser } = this.props;

        return(
            <div style={{height: 1000 }}>
                <Card bg='dark' text='light' style={{width: 500, margin: '0 auto', marginTop: 75}}>
                    <Card.Title style={{fontSize: 50, textAlign: 'center'}}>Welcome to the Strider General Store Management App</Card.Title>

                        {authedUser == null ? (
                            <Card.Body bg='secondary' style={{textAlign: 'center'}}>
                                <Card.Text>To continue please login</Card.Text>
                            </Card.Body>
                            ):(
                                    <div/>
                                    )}

                            </Card>
            </div>
        );
    }
}

function mapStateToProps({ customers, items, orders }, props){
    return {
        customers,
        items,
        orders,
        ...props
    }
}

export default connect(mapStateToProps)(Homepage);
