export const ADD_CUSTOMER = 'ADD_CUSTOMER';

function setCustomers (customers) {
    return {
        type: RECEIVE_DATA,
        customers
    }
}

function addCustomer (customer) {
    return {
        type: ADD_CUSTOMER,
        customer
    }
}
